package b_conditions_for_if_while;

public class b_Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * while(condition) {
		 * 
		 * }
		 */

		// print numbers from 1 to 10

		int i = 0;

		while (i <= 10) {
			System.out.println(i);
			i++; // i=1 , i=2
		}
		
		System.out.println("*******************************");

		int j = 10;

		while (j > 0) {
			System.out.println(j);
			j--; // i=1 , i=2
		}

	}

}
